template.value:
ALTER TABLE $TABLE$
	ADD COLUMN $FIELD$_f BOOL NOT NULL AS ($FIELD$::BOOL) STORED;
ALTER TABLE $TABLE$
	ALTER COLUMN $FIELD$_f DROP STORED,
	RENAME COLUMN $FIELD$ TO $FIELD$_bk,
	RENAME COLUMN $FIELD$_f TO $FIELD$,
	ALTER COLUMN $FIELD$ SET DEFAULT false;
ALTER TABLE $TABLE$
	DROP COLUMN IF EXISTS $FIELD$_bk;
