template.value:
try {
      const dataToSend: Query_DD_Variables = {};
      let res = await this.$apollo.mutate({
                                      mutation:  Query_UpdateAutomation,
                                      variables: dataToSend
                                    });
      this.$toast({
                    type:         "success",
                    message:      "The automation has been archived",
                    linkRouteObj: null,
                    linkText:     null
                  });
      this.close();
    } catch (e) {
      this.$toast({
                    type:         "error",
                    message:      "There was an error while archiving. Please try again",
                    linkRouteObj: null,
                    linkText:     null
                  });
    }

try {
      const dataToSend: QL_Query_MutationVariables = {};
      let res = await this.$apollo.mutate({
                                      mutation:  Query_UpdateAutomation,
                                      variables: dataToSend,
                                      update:    (store, {data}) => {
                                              const dataInCache = store.readQuery({
                                                                                    query:     Query_Team_Pending_Invitations,
                                                                                    variables: {
                                                                                      team_id: this.current_team.id
                                                                                    }
                                                                                  });
                                              dataInCache.team_pending_invitations = dataInCache.team_pending_invitations.filter(
                                                  t => {
                                                    return t.id != this.invitation.id;
                                                  });

                                              store.writeQuery({
                                                                 query:     Query_Team_Pending_Invitations,
                                                                 variables: {
                                                                   team_id: this.current_team.id
                                                                 },
                                                                 data:      dataInCache
                                                               });
                                            }
                                    });
      this.$toast({
                    type:         "success",
                    message:      "The automation has been archived",
                    linkRouteObj: null,
                    linkText:     null
                  });
      this.close();
    } catch (e) {
      this.$toast({
                    type:         "error",
                    message:      "There was an error while archiving. Please try again",
                    linkRouteObj: null,
                    linkText:     null
                  });
    }
